import { useRouter } from "next/router";
import { useState, useEffect } from "react";
import Layout from "../../../components/Layout";
import Loader from "../../../components/Loader";

function Release(props) {
  const [selectedRelease, setSelectedRelease] = useState(null);
  const router = useRouter();
  const { releaseName } = router.query;
  useEffect(() => {
    props.releases &&
      props.releases.map(function(release) {
        if (release.slugs.includes(releaseName)) {
          setSelectedRelease(release);
        }
      });
  }, [props]);

  return (
    <div className="main-container">
      {!selectedRelease ? (
        <Loader />
      ) : (
        <Layout>
          <div className="release-page-container">
            <img className="release-page-logo" src="/img/logo.jpg" alt="logo" />
            <div>
              <img
                className="release-page-cover"
                src={selectedRelease.data.cover.url.slice(0, -21)}
                alt="release-cover"
              />
              <div className="release-page-details">
                <div className="release-artist-name">
                  {selectedRelease.data.artist[0].text}
                </div>
                <div className="release-song-title">
                  {selectedRelease.data.title[0].text}
                </div>
              </div>
            </div>

            <style jsx>{`
              .main-container {
                height: 100vh;
              }
              .release-page-container {
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: center;
                width: 100%;
                font-family: var(--font1);

                text-transform: uppercase;
                line-height: 1.2rem;
                letter-spacing: 0.05rem;
                padding-left: 0.5rem;
                padding-right: 0.5rem;
                text-align: center;
              }
              .release-page-logo {
                height: 5rem;
                width: auto;
              }

              .release-page-cover {
                width: 100%;
              }
              .release-page-details {
                text-align: center;
              }
              .release-artist-name {
                margin-top: 1.5rem;
                font-size: 1.5rem;
              }
              .release-song-title {
                margin-top: 1.5rem;
                font-size: 1rem;
              }
            `}</style>
          </div>
        </Layout>
      )}
    </div>
  );
}

export default Release;
