import { useRouter } from "next/router";
import Layout from "../../../components/Layout";
import Loader from "../../../components/Loader";

function Release(props) {
  const router = useRouter();
  const { releaseName } = router.query;
  let selectedRelease = {};
  props.releases &&
    props.releases.map(
      release =>
        release.slugs.includes(releaseName) && (selectedRelease = release)
    );
  console.log(selectedRelease);
  return (
    selectedRelease && (
      <Layout>
        <div className="release-page-container">
          <img className="release-page-logo" src="/img/logo.jpg" alt="logo" />
          <div>
            <img
              className="release-page-cover"
              src={selectedRelease.data.cover.url.slice(0, -21)}
              alt="release-cover"
            />
            <div className="release-page-details">
              <div className="release-artist-name">
                {selectedRelease.data.artist[0].text}
              </div>
              <div className="release-page-name">
                {selectedRelease.data.title[0].text}
              </div>
            </div>
          </div>

          <style jsx>{`
            .release-page-container {
              display: flex;
              flex-direction: column;
              align-items: center;
            }
            .release-page-logo {
              height: 5rem;
              width: auto;
            }
          `}</style>
        </div>
      </Layout>
    )
  );
}

export default Release;
