import Header from "./Header";
import Head from "next/head";
import GlobalStyles from "../style/GlobalStyles";
import { initGA, logPageView } from "./utils/analytics";
import { useEffect } from "react";
import landingText from "./text/landingText";

function Layout(props) {
  useEffect(() => {
    if (!window.GA_INITIALIZED) {
      initGA();
      window.GA_INITIALIZED = true;
    }
    logPageView();
  }, [window.GA_INITIALIZED]);
  return (
    <div>
      <Head>
        <meta property="og:type" content="website" />
        <meta name="og:title" property="og:title" content="Elis Records" />
        <meta
          name="og:description"
          property="og:description"
          content={landingText}
        />
        <meta property="og:site_name" content="Elis Records" />
        <meta property="og:url" content="http://www.elis-records.com/" />
        <meta
          property="og:image"
          content="http://www.elis-records.com/img/elis-socials-img-tag.jpg"
        />
        <meta
          name="twitter:image"
          content="http://www.elis-records.com/img/elis-socials-img-tag.jpg"
        />
        <title>Elis Records</title>
        <meta name="description" content={landingText} />
        <link rel="icon" type="image/png" href="/img/favicon.ico" />
        <link rel="apple-touch-icon" href="/img/favicon.ico" />
        <link rel="stylesheet" href="/css/priority.css" />
      </Head>

      <div className="container">
        <Header />
        {props.children}
        <div className="footer">
          <p className="email-address">ola@elis-records.com</p>
          <div className="open-source">
            built by{" "}
            <a
              className="overdub-link"
              href="https://www.overdub.dev/"
              target="_blank"
            >
              overDub.dev
            </a>{" "}
            with next.js and open-sourced on{" "}
            <a
              className="github-link"
              href="https://github.com/jeremieemk/elis-records-website"
              target="_blank"
            >
              GitHub
            </a>
          </div>
        </div>
      </div>
      {GlobalStyles}
      <style jsx>{`
        .container {
          position: relative;
          width: 100%;
        }
        .footer {
          bottom: -2;
          margin-bottom: 10rem;
          margin-top: 3rem;
          text-align: center;
          color: black;
          font-family: var(--font1);
          font-size: var(--standard-font-size);
          padding-left: 2rem;
          padding-right: 2rem;
          letter-spacing: 0.05rem;
        }
        .github-link,
        .overdub-link {
          color: var(--color2);
        }
        .open-source {
          margin-bottom: 0.5rem;
          opacity: 0.7;
        }
        .email-address {
          margin-bottom: 1rem;
        }
        @media (min-width: 801px) {
          .footer {
            font-size: 1.1rem;
          }
        }
      `}</style>
    </div>
  );
}

export default Layout;
