const GlobalStyles = (
  <style jsx global>{`
    @font-face {
      font-family: "PxGroteskBold";
      src: url("fonts/PxGrotesk-Bold.woff2") format("woff2");
      src: url("fonts/PxGrotesk-Bold.woff") format("woff");
      src: url("fonts/PxGroteskBold.ttf") format("truetype");
    }
    @font-face {
      font-family: "Sectra";
      src: url("fonts/Sectra.woff2") format("woff2");
      src: url("fonts/Sectra.woff") format("woff");
      src: url("fonts/Sectra.ttf") format("truetype");
    }
    @font-face {
      font-family: "WorkSans-Bold";
      src: url("fonts/WorkSans-Bold.woff2") format("woff2");
      src: url("fonts/WorkSans-Bold.woff") format("woff");
      src: url("fonts/WorkSans-Bold.ttf") format("truetype");
    }
    @font-face {
      font-family: "WorkSans-Regular";
      src: url("fonts/WorkSans-Regular.ttf") format("truetype");
    }

    :root {
      --global-margin: 2rem;
      --desktop-padding: 25%;
      --color1: #f7f7f7;
      --color2: #f74d2c;
      --font1: "Sectra";
      --font2: "PxGroteskBold";
      --font3: "WorkSans-Bold";
      --font4: "WorkSans-Regular";
      --line-width: 0.1rem;
    }
    body {
      background-color: var(--color1);
      margin: 0;
    }
    a {
      text-decoration: none;
      color: inherit;
    }

    input[type="range"] {
      -webkit-appearance: none; /* Hides the slider so that custom slider can be made */
      width: 100%; /* Specific width is required for Firefox. */
      background: transparent; /* Otherwise white in Chrome */
    }

    input[type="range"]::-webkit-slider-thumb {
      -webkit-appearance: none;
    }

    input[type="range"]:focus {
      outline: none; /* Removes the blue border. You should probably do some kind of focus styling for accessibility reasons though. */
    }

    input[type="range"]::-ms-track {
      width: 100%;
      cursor: pointer;

      /* Hides the slider so custom styles can be added */
      background: transparent;
      border-color: transparent;
      color: transparent;
    }
    input:focus,
    textarea:focus,
    select:focus {
      outline: none;
    }
    @keyframes fadeIn {
      from {
        opacity: 0;
      }

      to {
        opacity: 1;
      }
    }
  `}</style>
);

export default GlobalStyles;
