import { useState, useRef, useEffect } from "react";
import PlayButton from "./PlayButton";
import ProgressBar from "./ProgressBar";

function AudioPlayer() {
  const [playingStatus, setPlayingStatus] = useState(false);
  const [audioCurrentTime, setAudioCurrentTime] = useState(0);
  const audio = useRef(null);

  const handlePlayButtonClick = () => {
    setPlayingStatus(!playingStatus);
    setAudioCurrentTime(audio.current.currentTime);
  };
  playingStatus && setAudioCurrentTime(audio.current.currentTime);

  useEffect(() => {
    playingStatus ? audio.current.play() : audio.current.pause();
  }, [playingStatus]);

  return (
    <div className="audio-player-container">
      <PlayButton
        handlePlayButtonClick={handlePlayButtonClick}
        playingStatus={playingStatus}
      />
      <ProgressBar audioCurrentTime={audioCurrentTime} />
      <audio ref={audio} src={"/music/0.mp3"} />
      {style}
    </div>
  );
}

