import Layout from "../components/Layout";
import Head from "next/head";
import LandingText from "../components/LandingText";
import ReleaseEntry from "../components/ReleaseEntry";
import Prismic from "prismic-javascript";
import { useEffect, useState } from "react";
import Loader from "../components/Loader";
import landingText from "../components/text/landingText";

function Index() {
  const [releases, setReleasesData] = useState(null);
  const [dataIsLoaded, setDataIsLoaded] = useState(false);
  const apiEndpoint = "https://elis-records.prismic.io/api/v2";
  const accessToken = "";
  const Client = Prismic.client(apiEndpoint, { accessToken });
  useEffect(() => {
    const fetchData = async () => {
      const response = await Client.query(
        Prismic.Predicates.at("document.type", "release"),
        { orderings: "[my.release.release-date desc]" }
      );
      if (response) {
        setReleasesData(response.results);
        setDataIsLoaded(true);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      {" "}
      <Head>
        <meta property="og:type" content="website" />
        <meta name="og:title" property="og:title" content="Elis Records" />
        <meta
          name="og:description"
          property="og:description"
          content={landingText}
        />
        <meta property="og:site_name" content="Elis Records" />
        <meta property="og:url" content="http://www.elis-records.com/" />
        <meta
          property="og:image"
          content="http://www.elis-records.com/img/elis-socials-img-tag.jpg"
        />
        <meta
          name="twitter:image"
          content="http://www.elis-records.com/img/elis-socials-img-tag.jpg"
        />
        <title>Elis Records</title>
        <meta name="description" content={landingText} />
        <link rel="icon" type="image/png" href="/img/favicon.ico" />
        <link rel="apple-touch-icon" href="/img/favicon.ico" />
        <link rel="stylesheet" href="/css/priority.css" />
      </Head>
      <div className="main-container">
        {dataIsLoaded ? (
          <div className="loaded-site-container">
            <Layout>
              <LandingText />

              <ReleaseEntry releases={releases} />
            </Layout>
          </div>
        ) : (
          <Loader size="80" />
        )}
      </div>
      <style jsx>{`
        .main-container {
          height: 100vh;
        }
        .loaded-site-container {
          animation: fadeIn 1s;
          animation-fill-mode: forwards;
        }
        @keyframes fadeIn {
          from {
            opacity: 0;
          }

          to {
            opacity: 1;
          }
        }

        @media (min-width: 801px) {
          .main-container {
            padding-left: 25%;
            padding-right: 25%;
          }
        }
        @media (min-width: 2200px) {
          .main-container {
            padding-left: 35%;
            padding-right: 35%;
          }
        }
      `}</style>
    </div>
  );
}

export default Index;
