import { useRouter } from "next/router";
import Layout from "../../../components/Layout";

function Release(props) {
  const router = useRouter();
  const { releaseName } = router.query;
  let selectedRelease = {};
  props.releases &&
    props.releases.map(
      release =>
        release.slugs.includes(releaseName) && (selectedRelease = release)
    );
  console.log(selectedRelease);
  return (
    <Layout>
      <div className="release-page-container">
        <img className="release-page-logo" src="/img/logo.jpg" alt="logo" />
        <div>
          <h1>Release: {releaseName}</h1>
        </div>
      </div>

      <style jsx>{`
        .release-page-container {
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .release-page-logo {
          height: 5rem;
          width: auto;
        }
      `}</style>
    </Layout>
  );
}

export default Release;
