import Header from "./Header";
import Head from "next/head";
import GlobalStyles from "../style/GlobalStyles";

const Layout = props => (
  <div>
    <Head>
      <script
        async
        src="https://www.googletagmanager.com/gtag/js?id=UA-150696978-2"
      ></script>
      <script>
        window.dataLayer = window.dataLayer || []; function gtag()
        {dataLayer.push(arguments)}
        gtag('js', new Date()); gtag('config', 'UA-150696978-2');
      </script>
      <link rel="shortcut icon" href="/static/favicon.ico" />
      <link rel="stylesheet" href="/css/priority.css"></link>
    </Head>

    <div className="container">
      <Header />
      {props.children}
      <div className="footer">
        <p className="email-address">ola@elis-records.com</p>
        <div className="open-source">
          built by{" "}
          <a
            className="overdub-link"
            href="https://www.overdub.dev/"
            target="_blank"
          >
            overDub.dev
          </a>{" "}
          with next.js and open-sourced on{" "}
          <a
            className="github-link"
            href="https://github.com/jeremieemk/elis-records-website"
            target="_blank"
          >
            GitHub
          </a>
        </div>
      </div>
    </div>
    {GlobalStyles}
    <style jsx>{`
      .container {
        position: relative;
        width: 100%;
      }
      .footer {
        bottom: -2;
        margin-bottom: 10rem;
        margin-top: 3rem;
        text-align: center;
        color: black;
        font-family: var(--font1);
        font-size: var(--standard-font-size);
        padding-left: 2rem;
        padding-right: 2rem;
        letter-spacing: 0.05rem;
      }
      .github-link,
      .overdub-link {
        color: var(--color2);
      }
      .open-source {
        margin-bottom: 0.5rem;
        opacity: 0.7;
      }
      .email-address {
        margin-bottom: 1rem;
      }
      @media (min-width: 801px) {
        .footer {
          font-size: 1.1rem;
        }
      }
    `}</style>
  </div>
);

export default Layout;
